import { Transaction } from "../../transaction";
import { EIsolationLevel } from "../../types";
/**
 * mysql 事务类
 * @since 0.3.0
 */
declare class MysqlTransaction extends Transaction {
    /**
     * 设置当前事务
     * @param isolation 事务隔离级
     * @since           0.4.0
     */
    setIsolationLevel(isolationLevel: EIsolationLevel): Promise<void>;
    /**
     * 开始事务
     * @throws      开始事务错误
     */
    begin(): Promise<void>;
    /**
     * 事务提交
     * @throws      提交事务错误
     */
    commit(): Promise<void>;
    /**
     * 事务回滚
     * @throws      回滚事务错误
     */
    rollback(): Promise<void>;
}
export { MysqlTransaction };
