import { Transaction } from "../../transaction";
import { ESqliteTransactionType } from "../../types";
/**
 * Sqlite 事务类
 * @since 0.4.0
 */
declare class SqliteTransaction extends Transaction {
    private type;
    /**
     * 设置sqlite事务类型（仅sqlite事务使用）
     * @param type  事务类型
     */
    setType(type: ESqliteTransactionType): void;
    /**
     * 开始事务
     * @param sqliteTransaction     事务类型
     */
    begin(): Promise<void>;
    /**
     * 事务提交
     */
    commit(): Promise<void>;
    /**
     * 事务回滚
     */
    rollback(): Promise<void>;
}
export { SqliteTransaction };
