import { Connection } from "./connection";
import { EIsolationLevel } from "./types";
/**
 * 事务基类
 */
declare abstract class Transaction {
    /**
     * 连接对象
     */
    conn: Connection;
    /**
     * 线程id
     */
    threadId: number;
    /**
     * 事务隔离级别
     */
    isolation: EIsolationLevel;
    /**
     * 构造器
     * @param conn  真实连接对象
     */
    constructor(conn: any);
    /**
     * 设置当前事务
     * @param isolation 事务隔离级
     */
    setIsolationLevel(isolationLevel: EIsolationLevel): Promise<void>;
    /**
     * 事务开始
     * @param sqliteTransaction sqlite事务类型
     */
    begin(): Promise<void>;
    /**
     * 事务提交,继承类需要重载
     */
    commit(): Promise<void>;
    /**
     * 事务回滚,继承类需要重载
     */
    rollback(): Promise<void>;
}
export { Transaction };
